const { readFileSync } = require("fs");
const { createServer } = require("http");
const { parse } = require("url");
const React = require("react");
const { renderToString } = require("react-dom/server");

const htmlTemplate = readFileSync(`${__dirname}/index.html`, "utf8");

const ssrItems = [
    { id: 1, name: 'item 1' },
    { id: 2, name: 'item 2' },
    { id: 3, name: 'item 3' },
    { id: 4, name: 'item 4' },
    { id: 5, name: 'item 5' },
];

// Function-based Component without JSX
function Home() {
    return React.createElement(
        "div",
        null,
        React.createElement("h1", null, "Test SSR Items"),
        React.createElement(
            "ul",
            null,
            ssrItems.map((item) =>
                React.createElement("li", { key: item.id }, item.name)
            )
        )
    );
}

const server = createServer((req, res) => {
    const pathName = parse(req.url, true).pathname;
    
    if (pathName === "/") {
        const renderedHtml = renderToString(React.createElement(Home));
        const fullPage = htmlTemplate.replace("{{content}}", renderedHtml);
        
        res.writeHead(200, {
            "Content-Type": "text/html",
        });
        res.end(fullPage);
    } else if (pathName === "/test") {
        res.end("Test");
    } else {
        res.writeHead(404, {
            "Content-Type": "text/plain",
        });
        res.end("Not Found");
    }
});

server.listen(8000, () => console.log("Listening for requests on port 8000"));

